mapper.select:
select
id, file_url, imm_req_json, created_time, is_deleted
from tb_oss_upload


        and id = #{id}


        and file_url = #{fileUrl}


        and imm_req_json = #{immReqJson}


        and created_time = #{createdTime}


        and is_deleted = #{isDeleted}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from tb_oss_upload


        and id = #{id}


        and file_url = #{fileUrl}


        and imm_req_json = #{immReqJson}


        and created_time = #{createdTime}


        and is_deleted = #{isDeleted}
